package com.github.adrianjesussilva.textimageforge;

import static org.junit.jupiter.api.Assertions.assertDoesNotThrow;

import java.awt.Color;
import java.awt.Font;
import java.io.File;
import java.io.IOException;

import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.MethodOrderer;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.TestMethodOrder;

import com.github.adrianjesussilva.textimageforge.enumerator.TextAlign;
import com.github.adrianjesussilva.textimageforge.logic.exception.InvalidTextForgeConfigException;
import com.github.adrianjesussilva.textimageforge.logic.image.ImageForge;

import lombok.extern.log4j.Log4j2;

/**
 * Test case of the image forge 
 * 
 * by the nature of the porpuse of the implementation, the test case should validate manually until is implemented a computer vision checker
 * 
 * @author Adrian Jesus Simoes Silva 
 *
 */
@Log4j2
@TestMethodOrder(MethodOrderer.MethodName.class)
class TestImageForge {

	private static final Font FUENTE = new Font("Courier Prime",Font.PLAIN, 12); 


	public void voucherImage(String numControl, String [] lineas, String firma) throws IOException {
		String tmpdir = System.getProperty("java.io.tmpdir") + File.separator + "vpos-server";
		File carpeta = new File(tmpdir);
		log.info("Carpeta de vouchers" + carpeta);
		if (!carpeta.exists()) {
			carpeta.mkdir();
			log.info("Directorio temporal inexistente, se crea" + carpeta);
		}

		// recuperamos el objecto forgador
		log.info("Creando imagen de voucher");
		ImageForge forge;
		try {
			forge = new ImageForge(firma);
			// entregamos el texto correspondiente
			// log.info("Colocando texto de voucher");
			for (String linea : lineas) {
				// proceso para verificacion de firma
				// realizamos las verificaciones de las lineas
				if (linea.startsWith(" ", 1)) {
					linea = clearWhiteSpace(linea);
					forge.addLine(linea + "    ", null, TextAlign.CENTER, FUENTE, Color.BLACK);
				} else if (linea.equals("")) {
					forge.addLine(" ", null, TextAlign.CENTER, FUENTE, Color.BLACK);
				} else {
					forge.addLine(linea + "    ", null, TextAlign.LEFT, FUENTE, Color.BLACK);
				}
			}

			// guardamos vouchers en archivo
			String archivo = tmpdir + File.separator + numControl + ".png";
			forge.forgeImage(archivo, "");

			// Creamios la imagen y la entregamos
			log.info("Renderizando y entregando imagen obtenida " + numControl);
		} catch (InvalidTextForgeConfigException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}




	}

	/**
	 * funcion que elimina los espacios en blanco iniciales de una cadena
	 * @param cadena (String) cadena a tratar
	 * @return (String) la cadena tratada
	 */
	private static String clearWhiteSpace(String cadena){
		if(!cadena.equals("")){
			StringBuilder sb = new StringBuilder(cadena);

			int end = 0;
			for(end=0; end<cadena.length(); end++){
				if(cadena.charAt(end)!=' '){break;}
			}

			sb.delete(0, end);

			return sb.toString();
		}

		return cadena;

	}
	/**
	 * Test to validate image generation with signature
	 */
	@Test
	@DisplayName("Test 00 Validation test")
	void test00ValidationTest () {
		log.info("Starting validation of voucher generation with signature");
		String numControl = "123455";
		String [] lineasVoucher={"","        VOUCHER 0 "," ", "this is a voucher"};
		String firma = "";
		assertDoesNotThrow(() -> voucherImage(numControl,lineasVoucher , firma));
		log.info("Ending validation of voucher generation");
	}

	/**
	 * Test to validate image generation without signature
	 */
	@Test
	@DisplayName("Test 01 Validation test")
	void test01ValidationTest () {
		log.info("Starting validation of voucher generation");
		String numControl = "123456";
		String [] lineasVoucher={"","        VOUCHER 1 "," ", "this is a voucher"};
		assertDoesNotThrow(() -> voucherImage(numControl,lineasVoucher , null));
		log.info("Ending validation of voucher generation");
	}



}
